import React from "react";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            count: 0,
        };
    }

    render() {
        return (
            <div className="counter">
                <div className="counter__inner">
                    <div className="counter__bg">
                        <span className="counter__digit">
                            {this.state.count}
                        </span>
                        <div className="counter__buttons">
                            <button
                                className="button minus"
                                onClick={() =>
                                    this.setState({
                                        count: this.state.count - 1,
                                    })
                                }
                            >
                                - Minus
                            </button>
                            <button
                                className="button plus"
                                onClick={() => this.countFunc(1)}
                            >
                                + Plus
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    countFunc(task) {
        if (task === 1) {
            this.setState({
                count: this.state.count + 1,
            });
        } else {
            if (this.state.count == 0) {
                console.log("Nope");
            } else {
                this.setState({
                    count: this.state.count - 1,
                });
            }
        }
    }
}

export default App;
